import GangWarBg from "./Components/GangWarBg";
export default {
  data() {
    return {
      data: null,
      localTimer: 0,
      interval: null
    };
  },
  components: {
    GangWarBg
  },
  watch: {
    "data.Timer": {
      handler(val) {
        if (val <= 0) {
          return;
        }
        this.localTimer = val;
        if (this.interval) {
          clearInterval(this.interval);
        }
        this.interval = setInterval(() => {
          this.localTimer--;
          if (this.localTimer <= 0) {
            this.localTimer = 0;
            clearInterval(this.interval);
          }
        }, 1000);
      }
    }
  },
  methods: {
    setData(data) {
      this.data = data;
    },
    setRecursiveData(data) {
      this.$recursiveAssign(data, this.data, true);
    },
    formatTime(sec) {
      if (!sec) {
        return 0;
      }
      const minutes = Math.floor(sec / 60);
      const seconds = sec % 60;
      return sec ? `${(10 > minutes ? "0" : "") + minutes}:${(10 > seconds ? "0" : "") + seconds}` : 0;
    }
  },
  mounted() {
    this.$onClient("W:GangWar:SetRecursiveData", this.setRecursiveData);
    this.$onClient("W:GangWar:SetData", this.setData);
    this.$callClient("C:GangWar:Ready");

