export default {
  template: `
    <div class="controls-list__square-item">
        <div class="controls-square__item-header card" v-html="displayImage">

        </div>
        <div class="controls-square__content">
            <div class="controls-square__content-title">
                <p v-html="displayTitle"></p>
            </div>
        </div>
        <div class="controls-square__button" @click="onClick">
            <div class="controls-square__button-content">
                <p class="controls-square__button-title">{{displayContent}}</p>
            </div>
        </div>
    </div> 
    `,
  props: {
    action: {
      type: Object,
      required: true
    }
  },
  computed: {
    displayTitle() {
      return this.action && this.action.title !== undefined ? this.action.title : ``;
    },
    displayContent() {
      return this.action && this.action.content !== undefined ? this.action.content : ``;
    },
    displayImage() {
      return this.action && this.action.image !== undefined ? this.action.image : ``;
    }
  },
  methods: {
    onClick() {
      this.$emit('onClick', this.action.id);
    }
  }
};
