import MembersHeader from "./MembersHeader.js";
import MembersRow from "./MembersRow.js";
import Pages from "../../../global/organization/MainPages.js";
export default {
  template: `
    <div class="controls-main__wrapper header" :class="{pages:showPages}">
        <MembersHeader
            :membersCount="membersCount">
        </MembersHeader>
        <div class="controls-main__content">   
            <transition-group name="controls-members-list" tag="div" class="controls-main__content-list">
                <MembersRow 
                    v-for="member in members" 
                    :key="getMemberKey(member)" 
                    :member="getMemberProps(member)">
                </MembersRow>
            </transition-group>
            <Pages
                v-if="showPages"
                :page="membersPage"
                :maxPages="membersPagesCount"
                @setPage="setPage">
            </Pages>
        </div>
    </div>
    `,
  components: {
    MembersHeader,
    MembersRow,
    Pages
  },
  computed: {
    showPages() {
      return this.membersPagesCount && this.membersPagesCount !== undefined && this.membersPagesCount > 1 ? true : false;
    }
  },
  methods: {
    getMemberKey(member) {
      return member && member.id !== undefined ? member.id : 0;
    },
    getMemberProps(member) {
      return member !== undefined ? member : {};
    },
    setPage(type) {
      this.$EBus.$emit("src/components/controls/organization/members/default/MembersDefault->src/mixins/controls/organization/Members:MembersSetPage", this.membersPage, type);
    }
  },
  props: {
    members: {
      type: Array,
      required: true
    },
    membersCount: {
      type: Number,
      required: true
    },
    membersPage: {
      type: Number,
      required: true
    },
    membersPagesCount: {
      type: Number,
      required: true
    }
  }
};
