import "./Tablet.scss";
import Header from "./Components/Header.vue";
export default {
  components: {
    Header
  },
  data() {
    return {
      appActive: false
    };
  },
  methods: {
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.appActive = false;
    },
    closeApp() {
      this.$router.push("/");
      this.$callClient("Tablet:CloseBrowser");
    }
  },
  mounted() {
    this.appActive = true;
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
