import FurnitureTopSection from "./Сomponents/FurnitureTopSection.vue";
import FurnitureStoreMainSection from "./Сomponents/FurnitureStoreMainSection.vue";
import FurnitureInteriorMainSection from "./Сomponents/FurnitureInteriorMainSection.vue";
export default {
  data() {
    return {
      type: this.$route.params.type,
      playerinfo: null
    };
  },
  components: {
    FurnitureTopSection,
    FurnitureStoreMainSection,
    FurnitureInteriorMainSection
  },
  methods: {
    setPlayerinfo(info) {
      this.playerinfo = info;
      this.$store.dispatch("setFurnitureData");
    },
    closeApp() {
      this.$callClient("C:FurnitureStore:Close");
    },
    setSelectedCategory(categoryId) {
      this.selectedCategory = categoryId;
      this.selectedItem = "";
      this.selectedMultiplication = "1";
      this.furnituresList = [];
      this.$callClient("C:FurnitureStore:setCategory", this.selectedCategory);
    },
    setSelectedItem(id) {
      this.selectedItem = id;
      this.selectedMultiplication = "1";
    },
    setMultiplication(multiplication) {
      this.selectedMultiplication = multiplication;
    },
    setItemList(data) {
      this.furnituresList = data;
    },
    getImgPath() {
      return this.furnituresList.find(furniture => furniture.Identifier === this.selectedItem).ImagePath;
    },
    getCategoryPath(path) {
      return this.getVuexGettersgetCdnResolver.serverLink + path.replace("cdn/", "");
    },
    getCategoryName(categoryId) {
      return this.categoriesList.find(category => category.id === categoryId)?.name || "";
    },
    getFurnitureName(furnituresId) {
      return this.furnituresList.find(furniture => furniture.Identifier === furnituresId)?.Name || "";
    },
    getFurniturePath(name) {
      return require(`./Assets/${name}.png`);
    },
    formatNumberToCurrency(number) {
      let formattedNumber = number.toString().replace(".", ",");
      formattedNumber = formattedNumber.replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1.");
      formattedNumber = formattedNumber + " $";
      return formattedNumber;
    },
    onBuyFurniture(type) {
      const buyData = {
        isCash: type === "cash",
        count: this.selectedMultiplication,
        furniture: this.selectedItem
      };
      this.$callClient("C:FurnitureStore:Buy", buyData);
    },
    setData(data) {
      this.data = Object.assign(this.$data, data);
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.closeApp();
      }
    }
  },
  beforeUnmount() {
    window.removeEventListener("keydown", this.onKeyUp);
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
  }
};
