import ColorPicker from "./ColorPicker";
export default {
  data() {
    return {
      colorList: {
        1: "#FF0000",
        2: "#FF7F00",
        3: "#FFFF00",
        4: "#7FFF00",
        5: "#00FF00",
        6: "#00FF7F",
        7: "#00FFFF",
        8: "#007FFF",
        9: "#0000FF",
        10: "#7F00FF",
        11: "#FF00FF",
        12: "#FF007F",
        13: "#FF0000",
        14: "#FF7F00",
        15: "#FFFF00",
        16: "#7FFF00",
        17: "#00FF00",
        18: "#00FF7F"
      },
      selectedColor: "#FF0000"
    };
  },
  components: {
    ColorPicker
  },
  methods: {
    onInput(e) {
      if (this.isValidHex(e.target.value)) {
        this.selectedColor = e.target.value;
      }
    },
    isValidHex(hex) {
      // Регулярний вираз для перевірки шістнадцяткового коду
      const hexRegex = /^#[0-9A-F]{6}$/i;
      return hexRegex.test(hex);
    }
  }
};
