import Switcher from "../Components/Switcher.vue";
import BankInput from "../Components/BankInput.vue";
import Lock from "../Components/Lock.vue";
const Text = {
  business: {
    title: "Налог на бизнес",
    subtitle: "Каждый день необходимо платить налог\n на ваш бизнес. Сумма налога\n может отличаться исходя от типа бизнеса.\n Введите необходимую сумму для оплаты,\n а мы посчитаем на сколько дней вы оплатите."
  },
  house: {
    title: "Налог на недвижимость",
    subtitle: "Каждый день необходимо платить налог\n на вашу недвижимость. Сумма налога\n может отличаться исходя от типа недвижимости.\n Введите необходимую сумму для оплаты,\n а мы посчитаем на сколько дней вы оплатите."
  },
  mansion: {
    title: 'Налог на особняк',
    subtitle: 'Каждый день необходимо платить налог \n на ваш особняк. Сумма налога \n может отличаться исходя от типа бизнеса. \n Введите необходимую сумму для оплаты, \n а мы посчитаем на сколько дней вы оплатите.'
  },
  office: {
    title: 'Налог на офис',
    subtitle: 'Каждый день необходимо платить налог \n на ваш офис. Сумма налога \n может отличаться исходя от типа бизнеса. \n Введите необходимую сумму для оплаты, \n а мы посчитаем на сколько дней вы оплатите.'
  }
};
export default {
  components: {
    Switcher,
    BankInput,
    Lock
  },
  data() {
    return {
      navigationTabs: [{
        name: "Недвижимость",
        route: "house",
        image: "house"
      }, {
        name: "Бизнес",
        route: "business",
        image: "business"
      }, {
        name: 'Офис',
        route: 'office',
        image: 'office'
      }, {
        name: 'Особняк',
        route: 'mansion',
        image: 'mansion'
      }],
      activeTab: null,
      inputAmount: ""
    };
  },
  computed: {
    getText() {
      return Text[this.activeTab.route];
    },
    getVuexGettersGetBankTaxes() {
      return this.$store.getters.getBankTaxes;
    },
    getData() {
      return this.getVuexGettersGetBankTaxes[this.activeTab.route];
    },
    isAvailable() {
      return this.getData.tax != 0;
    },
    getDays() {
      return Math.floor(Number(this.inputAmount) / this.getData.tax);
    }
  },
  methods: {
    setActiveTab(tab) {
      this.activeTab = tab;
    },
    onAmountInput(e) {
      e.stopPropagation();
      if (!/^[0-9]+$/.test(e.target.value)) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      if (Number(e.target.value) > 1e9) e.target.value = 1e9 - 1;
      this.inputAmount = e.target.value;
    },
    onAction() {
      if (Number(this.inputAmount)) {
        this.$callClient("C:Bank:Taxes", this.activeTab.route, Number(this.inputAmount));
      }
    }
  },
  created() {
    this.activeTab = this.navigationTabs.find(x => this.$store.getters.getBankTaxes[x.route].tax != 0);
    this.$callClient("C:Bank:TaxesReady");
  },
  beforeUnmount() {
    this.$callClient("C:Bank:TaxesReady");
  }
};
