import ColorPickerSL from "./ColorPickerSL";
import ColorPickerHue from "./ColorPickerHue";
import { hexToHsv, hsvToHex } from "../../Assets/hsvUtils";
export default {
  data() {
    return {
      colorHsv: {
        h: 0,
        s: 0,
        v: 0
      }
    };
  },
  components: {
    ColorPickerSL,
    ColorPickerHue
  },
  props: {
    selectedColor: {
      type: String,
      default: "#36ced7"
    }
  },
  computed: {
    hexColor() {
      return hsvToHex(this.colorHsv.h, this.colorHsv.s, this.colorHsv.v);
    }
  },
  methods: {
    onChangeSL(color) {
      this.colorHsv = color;
    },
    onChangeHue(h) {
      this.colorHsv.h = h;
    },
    setHsvColor(h, s, v) {
      this.colorHsv = {
        h,
        s,
        v
      };
    }
  },
  mounted() {
    this.setHsvColor(...hexToHsv(this.selectedColor));
  }
};
