import PageTitle from "./Components/PageTitle.vue";
import Glow from "./Components/Glow.vue";
export default {
  data() {
    return {
      timer: 66,
      teamData: {
        name: "Pacific Gang",
        maxPlayers: 2
      },
      playerList: [{
        name: "STIV_HAYSON",
        id: 456,
        lvl: 15
      }, {
        name: "STIV_HAYSON",
