        Deaths: 2,
        XP: 450
      }],
      gameResultData: {
        place: 5,
        countOfTeam: 46
      },
      isShowModal: false,
      prizeData: null
    };
  },
  computed: {
    isResult() {
      return this.$route.params.step == 2;
    },
    isPrize() {
      return this.$route.params.step == 3;
    }
  },
  components: {
    PageTitle,
    Glow
  },
  methods: {
    formatSeconds(seconds) {
      const minutes = Math.floor(seconds / 60);
      const remainingSeconds = seconds % 60;
      return [minutes < 10 ? "0" + minutes : minutes, remainingSeconds < 10 ? "0" + remainingSeconds : remainingSeconds];
    },
    formatMoney(amount) {
      return amount.toLocaleString("en-US", {
        style: "currency",
        currency: "USD",
        minimumFractionDigits: 0
      }).replace("$", " $").replace(/,/g, ".");
    },
    setData(data) {
      this.teamData = data;
    },
    setPlayerList(data) {
      this.playerList = data;
    },
    setGameResultData(data) {
      this.gameResultData = data;
    },
    setTimer(data) {
      this.timer = data;
    },
    onContinue() {
      this.$callClient("C:BattleRoyaleLobby:Continue");
    },
    close() {
      this.$callClient("C:BattleRoyaleLobby:Close");
    },
    setPrizeData(data) {
      this.prizeData = data;
    },
    onkeydown(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.close();
      }
    }
  },
  mounted() {
    this.$onClient("C:BattleRoyaleLobby:SetData", this.setData);
    this.$onClient("C:BattleRoyaleLobby:SetPlayerList", this.setPlayerList);
    this.$onClient("C:BattleRoyaleLobby:SetGameResultData", this.setGameResultData);
    this.$onClient("C:BattleRoyaleLobby:SetTimer", this.setTimer);
    this.$onClient("C:BattleRoyaleLobby:SetPrizeData", this.setPrizeData);
    window.addEventListener("keyup", this.onkeydown);
    this.$callClient("C:BattleRoyaleLobby:Ready");
  },
  beforeUnmount() {
    this.$offClient("C:BattleRoyaleLobby:SetData", this.setData);
    this.$offClient("C:BattleRoyaleLobby:SetPlayerList", this.setPlayerList);
    this.$offClient("C:BattleRoyaleLobby:SetGameResultData", this.setGameResultData);
    this.$offClient("C:BattleRoyaleLobby:SetTimer", this.setTimer);
    this.$offClient("C:BattleRoyaleLobby:SetPrizeData", this.setPrizeData);
    window.removeEventListener("keyup", this.onkeydown);
  }
};
