import SOS from "../../Assets/Icons/SOS.vue";
import Arrow from "../../Assets/Icons/Arrow.vue";
import Popup from "../Popup/Popup.vue";
export default {
  components: {
    SOS,
    Arrow,
    Popup
  },
  data() {
    return {
      types: [{
        id: "global",
        content: "Глобальный код"
      }, {
        id: "local",
        content: "Локальный код"
      }],
      popupItems: [{
        id: 1,
        title: "Локальный код:",
        subtitle: "Отправляет вызов только для сотрудников организации в которой вы состоите"
      }, {
        id: 2,
        title: "Глобальный код:",
        subtitle: "Отправляет вызов всем сотрудникам из гос.фракций"
      }],
      activeType: {
        id: "global",
        content: "Глобальный код"
      }
    };
  },
  methods: {
    closeModal() {
      this.$emit("closeModal");
    },
    setType(type) {
      let index = this.types.indexOf(this.activeType);
      if (type === "next") {
        if (this.types[index + 1]) this.activeType = this.types[index + 1];else this.activeType = this.types[0];
      } else {
        if (this.types[index - 1]) this.activeType = this.types[index - 1];else this.activeType = this.types[this.types.length - 1];
      }
    },
    sendCode() {
      this.$emit("sendCode", this.activeType.id);
    }
  },
  mounted() {
    this.activeType = this.types[0];
  }
};
