import "core-js/modules/es.array.push.js";
import ListItem from "../Components/History/ListItem.vue";
export default {
  components: {
    ListItem
  },
  inject: ["history"],
  data() {
    return {
      tabs: [{
        id: "all",
        path: "/atm/history/all",
        name: "Все операции"
      }, {
        id: "put",
        path: "/atm/history/put",
        name: "Пополнения"
      }, {
        id: "get",
        path: "/atm/history/get",
        name: "Снятия"
      }, {
        id: "transfer",
        path: "/atm/history/transfer",
        name: "Переводы"
      }]
    };
  },
  computed: {
    operationsType() {
      return this.$route.params.type;
    },
    historyList() {
      return this.$store.getters["playerMenu/getBankHistory"][this.operationsType] || [];
    }
  },
  methods: {
    updateHistory(tabId) {
      if (this.history[tabId] == undefined) this.$callClient("C:ATM:GetHistory", tabId);
    },
    isTabActive(tab) {
      return this.operationsType == tab.id;
    },
    setActiveTab(tab) {
      if (!this.isTabActive(tab)) {
        this.updateHistory(tab.id);
        this.$router.push(tab.path);
      }
    }
  },
  created() {
    this.updateHistory("all");
    this.$callClient("C:ATM:HistoryReady");
  },
  beforeUnmount() {
    this.$callClient("C:ATM:HistoryDestroyed");
  }
};
