import "core-js/modules/es.array.push.js";
import MainCard from "./Components/MainCard.vue";
import ObjectCard from "./Components/ObjectCard.vue";
import UniversalModal from "../../../Components/UniversalModal/UniversalModal.vue";
export default {
  components: {
    MainCard,
    ObjectCard,
    UniversalModal
  },
  data() {
    return {
      realityTypes: [],
      activeRealityType: "",
      reality: [],
      price: 150,
      modalOpen: false,
      modalOption: {}
    };
  },
  methods: {
    setData(payload) {
      this.$recursiveAssign(payload, this.$data);
    },
    onAction() {
      if (!this.activeRealityType) this.OnClose();else {
        this.activeRealityType = "";
        this.reality = [];
      }
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.OnClose();
    },
    OnClose() {
      this.$router.push("/");
    },
    onMainAction(realityType) {
      this.$callClient("C:Reality:SetActiveType", realityType);
      this.activeRealityType = realityType;
    },
    onObjectAction(item) {
      this.$callClient("C:Reality:GetHouseInfo", item);
      // this.OpenModal(item);
    },
    OpenModal(item) {
      this.modalOption = item;
      this.modalOpen = true;
    },
    onModalAction() {
      this.$callClient("C:Reality:GetDirections", this.modalOption);
      this.OnClose();
    },
    onModalClose() {
      this.modalOption = {};
      this.modalOpen = false;
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:Reality:SetData", this.setData);
    this.$onClient("W:Reality:OpenModal", this.OpenModal);
    this.$callClient("C:Reality:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("W:Reality:SetData", this.setData);
    this.$offClient("W:Reality:OpenModal", this.OpenModal);
    this.$callClient("C:Reality:Destroyed");
  }
};
