export default {
  props: {
    itemCard: {
      type: Number,
      required: false
    },
    imgs: {
      type: Array,
      required: true
    }
  },
  components: {},
  data() {
    return {
      selectedOptionIdx: null,
      imgsList: []
    };
  },
  methods: {
    onOption(optionIdx) {
      this.selectedOptionIdx = optionIdx;
      this.setImg();
    },
    setImg() {
      if (this.imgsList[this.selectedOptionIdx] === "default") {
        this.$emit("switch", "");
      } else {
        this.$emit("switch", this.getFormattedPath());
      }
    },
    getFormattedPath() {
      return `https://cdn.nexus-rp.net/MarketPlaceServer/public/images/${this.itemCard.id}_${this.imgsList[this.selectedOptionIdx]}.png`;
    }
  },
  mounted() {
    this.selectedOptionIdx = 0;
    this.imgsList = [...this.imgs];
    if (this.itemCard?.objectType !== "service") {
      this.imgsList.unshift("default");
    }
  }
};
