import DefaultLogo from "./DefaultLogo.js";
export default {
  template: `
    <div class="store-navigation__wrapper navigation">
        <DefaultLogo
            :title="title"
            :subtitle="subtitle">
        </DefaultLogo>
        <div class="store-navigation__main" :class="listClass">
            <div class="store-navigation__main-title">
                <p>Консультант:</p>
                <p>Здравствуйте! Что конкретно вас интересует?</p>
            </div>
            <div class="store-navigation__main-list">
                <div class="store-navigation-list__item" v-for="category in categories" v-html="getInner(category)" @click="selectCategory(category)"></div>
            </div>
        </div>
        <div class="store-navigation__buttons">
            <div class="store-navigation__buttons-item" @click="closeShop">
                <p>Выйти</p>
            </div>
        </div>
    </div>
    `,
  props: {
    categories: {
      type: Array,
      required: true
    },
    title: {
      type: String
    },
    subtitle: {
      type: String
    },
    type: {
      type: String
    }
  },
  components: {
    DefaultLogo
  },
  computed: {
    listClass() {
      return this.type;
    }
  },
  methods: {
    getInner(category) {
      return `${category.image}<p>${category.content}</p>`;
    },
    closeShop() {
      this.$emit('closeShop');
    },
    selectCategory(category) {
      this.$emit('selectCategory', category);
    }
  }
};
