export default {
  data() {
    return {};
  },
  props: {
    houseData: {
      type: Object,
      required: true
    }
  },
  methods: {
    onOpenDoor() {
      this.$callClient("C:HouseManager:OpenDoor", !this.houseData.doorsClosed);
    },
    getDataFromState(title) {
      return this.$store.getters["universalModal/getFormModalDataByTitle"](title);
    },
    onSendOccupancyReq() {
      const UniversalModalData = {
        title: this.$t("Отправка запроса на заселение"),
        isSetToStore: true,
        content: [{
          type: "input",
          options: {
            label: this.$t("Введите имя игрока"),
            valuesName: "playerName"
          }
        }],
        buttons: {
          submit: {
            name: this.$t("Отправить запрос"),
            handler: () => {
              const modalData = this.getDataFromState("playerName");
              this.$callClient("C:HouseManager:SendOccupancyReq", modalData);
            }
          },
          cansel: {
            name: this.$t("Отмена")
          }
        }
      };
      this.$UniversalModal.create(UniversalModalData);
    },
    onSell() {
      const UniversalModalData = {
        title: this.$t("Продажа дома"),
        content: [{
          type: "description",
          value: this.$t("Вы действительно хотите купить дом <span>№{0}</span>?", [this.houseData.houseNum])
        }],
        buttons: {
          submit: {
            name: this.$t("Да"),
            handler: () => {
              this.$callClient("C:HouseManager:Sell");
            }
          },
          cansel: {
            name: this.$t("Отмена"),
            handler: () => {}
          }
        }
      };
      this.$UniversalModal.create(UniversalModalData);
    },
    formatMoney(value) {
      return value.toString().replace(/\B(?=(\d{3})+(?!\d))/g, " ") + "$";
    }
  }
};
