import OfferItem from "./Components/OfferItem.vue";
export default {
  props: {
    offersList: {
      type: Array,
      required: true
    },
    type: {
      type: String,
      required: true
    }
  },
  components: {
    OfferItem
  },
  data() {
    return {
      titles: {
        offer: ["ID", "Автор", "К-сть", "Ціна за 1 шт.", "Таймер", "Дія"],
        complain: ["ID", "Автор", "Причина скарги", "Дія"]
      }
    };
  },
  mounted() {
    if (this.getIsHasState && this.type === "offer") {
      this.titles["offer"].splice(3, 0, "Стан");
    }
  },
  computed: {
    getIsHasState() {
      return this.offersList[0]?.state;
    }
  }
};
