let canvas, innerRanges, ctx;
function resize() {
  canvas.width = window.innerWidth;
  canvas.height = window.innerHeight;
}
function onResize() {
  resize();
}
export function setCanvas() {
  innerRanges = document.querySelectorAll(".PropertyExchange__block-info-busines-statistic-range-inner");
  canvas = document.getElementById("statistic-canvas");
  resize();
  ctx = canvas.getContext("2d");
  ctx.lineWidth = 2;
  window.addEventListener("resize", onResize());
}
export function drawLine() {
  setCanvas();
  ctx.beginPath();
  ctx.moveTo(0, 0);
  innerRanges.forEach((range, index) => {
    const offset = range.getBoundingClientRect();
    if (!index) {
      ctx.moveTo(offset.x + 6, offset.y + 6);
      return;
    }
    ctx.lineTo(offset.x + 6, offset.y + 6);
  });
  ctx.strokeStyle = "#2C3A3A66";
  ctx.stroke();
}
export function clearCanvas() {
  ctx.clearRect(0, 0, canvas.width, canvas.height);
  document.removeEventListener("resize", onResize());
}
