export default {
  data: function () {
    return {
      appActive: false,
      name: "",
      type: "",
      language: "",
      items: []
    };
  },
  methods: {
    onKeyUp(e) {
      if (e.key === "Escape") this.closeSearch();
    },
    closeSearch() {
      this.appActive = false;
    },
    destroyBrowser() {
      this.$callClient("Search:DestroyBrowser");
    },
    openSearch(items, name, type) {
      if (name !== undefined) this.name = name;
      this.items = items;
      this.type = type;
      this.appActive = true;
    },
    getOut(item) {
      if (item && item.id !== undefined && this.type == "search") this.$callClient("Search:GetOut", item.id);else if (item && item.id !== undefined) this.$callClient("FamilyVehicle:GetVehicle", item.id);
    },
    getOutAll() {
      this.$callClient("Search:GetOutAll");
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("C:Search:OpenSearch", this.openSearch);
    this.$onClient("C:Search:CloseSearch", this.closeSearch);
    this.$callClient("W:Search:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$offClient("C:Search:OpenSearch", this.openSearch);
    this.$offClient("C:Search:CloseSearch", this.closeSearch);
  }
};
