import { Map, View } from "ol";
import ImageLayer from "ol/layer/Image";
import Static from "ol/source/ImageStatic";
export default {
  props: {
    setMapObj: Function,
    typeOfMap: {
      type: String,
      default: "BW"
    }
  },
  mounted() {
    const imgWidth = 5000;
    const imgHeight = 7695;
    const maps = {
      BW: "./img/Family/Map/NarcoMap-min2.jpg",
      Color: "https://cdn.nexus-rp.net/family/customMap.jpg"
    };
    const imgLayer = new ImageLayer({
      source: new Static({
        // url: "./img/Family/Map/crimefields-copy.jpg",
        url: maps[this.typeOfMap],
        imageSize: [imgWidth, imgHeight],
        imageExtent: [-imgWidth / 2, -imgHeight / 2, imgWidth / 2, imgHeight / 2],
        size: [imgWidth, imgHeight]
      })
    });
    const view = new View({
      projection: "EPSG:3857",
      center: [0, 0],
      zoom: 15,
      maxZoom: 19,
      minZoom: 14,
      constrainOnlyCenter: true,
      extent: [-imgWidth / 3, -imgHeight / 3, imgWidth / 3, imgHeight / 2]
    });
    const mapCenter = view.getCenter();
    const shiftedCenter = [mapCenter[0] + 2, mapCenter[1] + 2];
    view.setCenter(shiftedCenter);
    const map = new Map({
      target: this.$refs.map,
      renderer: "webgl",
      layers: [imgLayer],
      view
    });
    this.$store.dispatch("setMap", map);

    // this.setMapObj(map);
  }
};
