export default {
  data() {
    return {
      scaleIconProcent: 0.83,
      procent: 0,
      tapMsg: "",
      exitMsg: ""
    };
  },
  methods: {
    getStrokeDashoffset(count, max) {
      const procent = count / max * 100;
      return 100 - procent;
    },
    setScaleIconProcent() {
      const fontSize = getComputedStyle(document.documentElement).fontSize;
      const size = fontSize.slice(0, -2);
      this.scaleIconProcent = +size / 10 + 0.04;
    },
    setData(data) {
      this.procent = data;
    },
    setMsg(tap, exit) {
      this.tapMsg = tap;
      this.exitMsg = exit;
    }
  },
  mounted() {
    window.addEventListener("resize", this.setScaleIconProcent);
    this.setScaleIconProcent();
    this.$onClient("C:TapTapMiniGame:UpdateProgress", this.setData);
    this.$onClient("C:TapTapMiniGame:SetMessages", this.setMsg);
    this.$callClient('C:TapTapMiniGame:Ready');
  },
  beforeUnmount() {
    this.$offClient("C:TapTapMiniGame:UpdateProgress", this.setData);
    this.$offClient("C:TapTapMiniGame:SetMessages", this.setMsg);
  }
};
