import { mapState, mapMutations, mapGetters } from "vuex";
import MenuClock from "./CurrentPlayerMenuClock";
import MenuRange from "./CurrentPlayerMenuRange";
export default {
  name: "CurrentPlayerMenu",
  components: {
    MenuClock,
    MenuRange
  },
  props: ["currentPlayer"],
  computed: {
    ...mapState("poker", ["currentPlayerMove", "currentPlayerMoveValues", "bankAvailable", "currentPlayerRaiseInput", "currentCheckValue", "autoCheck", "grantAutoCheck", "currentPlayer", "activePlayer", "otherPlayers"]),
    ...mapGetters("localization", ["loc"]),
    raiseInput: {
      get: function () {
        return this.currentPlayerRaiseInput;
      },
      set: function (value) {
        this.setCurrentPlayerRaiseInput(value);
      }
    },
    autoCheckIput: {
      get: function () {
        return this.autoCheck;
      },
      set: function (value) {
        return this.toggleAutoCheck(value);
      }
    }
  },
  methods: {
    ...mapMutations("poker", ["setCurrentPlayerRaiseInput", "incrementPlayerRaiseInput", "decrementPlayerRaiseInput", "toggleAutoCheck"]),
    fold: function () {
      window.mp.trigger("poker:fold");
    },
    check: function () {
      window.mp.trigger("poker:check");
    },
    raise: function (value) {
      window.mp.trigger("poker:raise", value);
    },
    allIn: function () {
      window.mp.trigger("poker:allIn");
    },
    exit: function () {
      const UniversalModalData = {
        title: this.$t("Выход из-за стола"),
        content: [{
          type: "description",
          value: this.$t("Вы действительно хотите выйти из-за стола?")
        }],
        buttons: {
          submit: {
            name: this.$t("Да"),
            handler: () => {
              window.mp.trigger("poker:exit");
            }
          },
          cansel: {
            name: this.$t("Нет")
          }
        }
      };
      this.$UniversalModal.create(UniversalModalData);
      // this.$emit("setExitWindow");
    },
    setCurrentPlayerVoise: function () {
      window.mp.trigger("poker:setCurrentPlayerVoise");
    }
  },
  mounted: function () {
    this.setCurrentPlayerRaiseInput(this.currentPlayerMoveValues.minValue);
  }
};
