export default {
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      items: Object.freeze({
        cName: {
          name: "Имя"
        },
        number: {
          name: "Паспорт №"
        },
        cSurname: {
          name: "Фамилия"
        },
        gender: {
          name: "Пол",
          fn: value => this.$t(value ? "М" : "Ж")
        },
        date: {
          name: "Дата рождения"
        },
        house: {
          name: "Место жительства",
          fn: value => value ? this.$t("Дом № {0}", [value]) : this.$t("Отсутсвует")
        }
      })
    };
  },
  computed: {
    getImage() {
      return this.data.gender ? "male" : "female";
    },
    getPartner() {
      return this.data.partner ? this.data.gender ? this.$t("Женат на {0}", [this.data.partner]) : this.$t("Замужем за {0}", [this.data.partner]) : this.data.gender ? this.$t("Не женат") : this.$t("Не замужем");
    },
    getSerial() {
      let date = this.data.date.replaceAll("/", "");
      let gender = this.data.gender ? "M" : "F";
      let number = this.data.number.toString();
      return `P &lt${this.data.cName}&lt&lt${this.data.cSurname}&ltNEXUS&lt&lt${gender}${"&lt".repeat(42 - this.data.name.length + 1)}<span>${number}NEXUS${date}${gender}${number}${number}&ltSSR${"&lt".repeat(47 - 3 * number.length - date.length)}</span>`;
    }
  }
};
