import "core-js/modules/es.array.push.js";
export default {
  el: "#app",
  data: function () {
    return {
      appActive: false,
      rows: []
    };
  },
  methods: {
    onKeyUp(e) {
      if (e.key === "Escape") this.appActive = false;
    },
    destroyBrowser() {
      this.$callClient("Lift:DestroyBrowser");
    },
    openLift(buttons) {
      let rows = [];
      let counter = Math.ceil(buttons.length / 7);
      for (let index = 1; index <= counter; index++) {
        rows.push(buttons.splice(0, 7));
      }
      this.rows = rows;
      this.appActive = true;
    },
    setActive(column) {
      this.$callClient("Lift:SetActive", column.id);
    },
    setActiveCallback(buttonID) {
      this.rows.forEach(row => row.forEach(column => {
        if (column.active) column.active = false;
        if (column.id === buttonID) column.active = true;
      }));
    }
  },
  mounted() {
    window.addEventListener("keyup", this.onKeyUp);
  }
};
