import { SVGImages } from "./Assets/icons.json";
import VueSlider from "../../../Components/CustomRange/CustomRange.vue";
const gasStationInfo = {
  name: "Заправка",
  owner: "Государство",
  showTickets: true,
  literPrice: 12,
  maxGas: 110,
  tickets: 5
};
export default {
  data() {
    return {
      countOfGas: 0,
      locale: "ua",
      isActive: false,
      ...gasStationInfo
    };
  },
  computed: {
    sumForPay() {
      return this.countOfGas * this.literPrice;
    }
  },
  components: {
    VueSlider
  },
  methods: {
    getImage(name) {
      return SVGImages[name];
    },
    buyGas(paymentType) {
      this.$callClient("Gas:Buy", paymentType, this.sumForPay);
      this.closeApp();
    },
    setCountOfGas(value) {
      this.countOfGas = value;
    },
    closeApp() {
      this.$router.push("/");
    },
    useTicket() {
      this.$callClient("Gas:UseTicket");
      this.closeApp();
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.closeApp();
      }
    },
    setData(data) {
      this.$recursiveAssign(data, this.$data);
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    // this.$onClient("Gas:SetData", this.setData);
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$callClient("Gas:Close");
  }
};
