import "core-js/modules/es.array.push.js";
import { SVGImages } from "./Assets/icons.json";
import VueSlider from "../../../Components/CustomRange/CustomRange.vue";
export default {
  data() {
    return {
      countOfGas: 0,
      locale: "ua",
      isActive: false,
      name: "Заправка",
      owner: "Государство",
      showTickets: false,
      literPrice: 12,
      maxGas: 110,
      tickets: 5
    };
  },
  computed: {
    sumForPay() {
      return (this.countOfGas + 1) * this.literPrice;
    }
  },
  components: {
    VueSlider
  },
  methods: {
    getImage(name) {
      return SVGImages[name];
    },
    buyGas(paymentType) {
      this.$callClient("Gas:Buy", paymentType, this.sumForPay);
      this.closeApp();
    },
    setCountOfGas(value) {
      this.countOfGas = value;
    },
    closeApp() {
      this.$router.push("/");
    },
    useTicket() {
      this.$callClient("Gas:UseTicket", this.countOfGas + 1);
      this.closeApp();
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.closeApp();
      }
    },
    setData(data) {
      this.isActive = true;
      this.$recursiveAssign(data, this.$data, true);
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("Gas:SetData", this.setData);
    this.$callClient("Gas:Ready");
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
    this.$callClient("Gas:Close");
  }
};
