import throttle from "../../functions/throttle.js";
export default {
  template: `
    <div>
        <slot></slot>
    </div>
    `,
  methods: {
    onMousemove(e) {
      let x = e.screenX / window.innerWidth;
      let y = e.screenY / window.innerHeight;
      for (let element of this.$el.children) {
        element.style.transform = `translateY(${(y * 10).toFixed(2)}px) translateX(${(x * 10).toFixed(2)}px)`;
      }
    }
  },
  mounted() {
    window.addEventListener('mousemove', throttle(15, this.onMousemove));
  }
};
