export default {
  data() {
    return {
      navigationTabs: Object.freeze([{
        route: "/businessTablet/main",
        title: "Информация \n о бизнесе",
        img: "main"
      }, {
        route: "/businessTablet/chart",
        title: "Инфографика",
        img: "chart"
      }]),
      hints: Object.freeze([{
        keys: ["Esc"],
        title: "Закрыть окно"
      }]),
      data: {
        data: {},
        actions: {},
        points: []
      }
    };
  },
  computed: {
    getData() {
      return (this.$route.fullPath.includes("main") ? ["data", "actions"] : ["points"]).reduce((acc, cur) => {
        acc[cur] = this.data[cur];
        return acc;
      }, {});
    }
  },
  methods: {
    setData(payload) {
      this.$recursiveAssign(payload, this.data, true);
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.$router.push("/");
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$onClient("W:BusinessTablet:SetData", this.setData);
    this.$callClient("C:BusinessTablet:Ready");
  },
  beforeUnmount() {
    window.addEventListener("keyup", this.onKeyUp);
    this.$offClient("W:BusinessTablet:SetData", this.setData);
    this.$callClient("C:BusinessTablet:Destroyed");
  }
};
