import PhoneMixin from '@/Application/Pages/Phone/Mixins/PhoneMixin.vue';
export default {
  name: "Sim",
  data() {
    return {
      inputVal: ""
      // Your data properties here
    };
  },
  mixins: [PhoneMixin],
  methods: {
    // Your methods here
    onInput(e) {
      const value = e.target.value;
      const onlyNumber = value.replace(/\D/g, "");
      const formatedNumber = onlyNumber > 6 ? onlyNumber.slice(0, 6) : onlyNumber;
      this.inputVal = formatedNumber.replace(/(\d{3})(\d{3})/, "$1-$2");
    },
    onInputNumber(e) {
      const value = e.target.value;
      const onlyNumber = value.replace(/\D/g, "");
      if (!onlyNumber) {
        this.numberVal = onlyNumber;
        return;
      }
      this.numberVal = this.formatMoney(onlyNumber) + "$";
    },
    onInputPhone(e) {
      const value = e.target.value;
      const onlyNumber = value.replace(/\D/g, "");
      const formatedNumber = onlyNumber > 7 ? onlyNumber.slice(0, 7) : onlyNumber;
      this.inputVal = this.formatNumber(formatedNumber);
      console.log(this.inputVal);
    },
    onlyNumber(str) {
      return str.replace(/\D/g, "");
    },
    onPay() {
      if (this.inputVal.length > 6 && this.numberVal.length > 1) {
        this.$callClient("W:Phone:BankApp:Pay", this.inputVal, this.numberVal);
      }
    },
    goToPay() {
      const num = this.onlyNumber(this.inputVal);
      this.$router.push(`/Phone/BankApp/Main/Pay/${num}/true`);
    }
  },
  mounted() {
    // Code to run when the component is mounted
  }
};
