import Robbery from "./Components/robbery/Robbery.vue";
import Aim from "./Components/AImView/AImView.vue";
import BattleRoyale from "./Components/BattleRoyale/BattleRoyale.vue";
import RhombusMenu from "./Components/RhombusMenu/RhombusMenu";
import Fisherman from "./Components/Fisherman/Fisherman.vue";
import GangWar from "./Components/GangWar/GangWar.vue";
import KillLog from "./Components/KillLog/KillLog.vue";
export default {
  components: {
    Robbery,
    Aim,
    BattleRoyale,
    RhombusMenu,
    Fisherman,
    GangWar,
    KillLog
  },
  data() {
    return {
      isOpenRobbery: false,
      isShowAim: false,
      isAiming: false,
      isShowBattleRoyale: false,
      isShowFisherman: false,
      isShowGangWar: false,
      isShowKillLog: false
    };
  },
  methods: {
    toggleRobbery(state) {
      this.isOpenRobbery = state;
    },
    toggleAim(isShow, isAiming) {
      this.isShowAim = isShow;
      this.isAiming = isAiming;
    },
    toggleBattleRoyale(data) {
      this.isShowBattleRoyale = data;
    },
    toggleFisherman(state) {
      this.isShowFisherman = state;
    },
    toggleGangWar(state) {
      this.isShowGangWar = state;
    },
    toggleKillLog(state) {
      this.isShowKillLog = state;
    }
  },
  created() {
    this.$onClient("W:ROBBERY:ToggleRobbery", this.toggleRobbery);
    this.$onClient("W:Aim:ToggleAim", this.toggleAim);
    this.$onClient("W:GangWar:ToggleGangWar", this.toggleGangWar);
    this.$onClient("W:KillLog:ToggleKillLog", this.toggleKillLog);
    this.$onClient("W:BattleRoyale:ToggleBattleRoyale", this.toggleBattleRoyale);
    this.$callClient("C:HUD:HudReady");
  },
  beforeUnmount() {
    this.$offClient("W:ROBBERY:ToggleRobbery", this.toggleRobbery);
    this.$offClient("W:Aim:ToggleAim", this.toggleAim);
    this.$offClient("W:GangWar:ToggleGangWar", this.toggleGangWar);
    this.$offClient("W:Fishermam:ToggleFisherman", this.toggleFisherman);
    this.$offClient("W:BattleRoyale:ToggleBattleRoyale", this.toggleBattleRoyale);
  }
};
