import "core-js/modules/es.array.push.js";
import { mapGetters } from "vuex";
import Select from "../../../Components/CustomSelect/CustomSelect.vue";
import BusinessWar__Info from "./Components/BusinessWar__Info.vue";
export default {
  components: {
    Select,
    BusinessWar__Info
  },
  data() {
    return {
      isShowModal: false,
      passedTime: 0,
      selectedCard: this.$t("Выберите карту"),
      attackData: {},
      attackId: null,
      businessData: [{
        coords: [-76.2111571613292, -635.060789499569],
        holdersName: "MobbDeep",
        isAttacked: true,
        attackerName: "EazyMFK_E",
        income: 10000,
        durationOfWar: 654,
        id: 17,
        name: "Гастроном"
      }, {
        coords: [500, 500],
        holdersName: "JordanPick",
        income: 10000,
        id: 11,
        name: "Гастроном"
      }],
      ownerName: "JordanPick",
      cartList: ["№1", "№5", "№3"]
    };
  },
  computed: {
    ...mapGetters({
      map: "getMap",
      familyName: "getFamilyName",
      vectorLayer: "getVectorLayer"
    }),
    countOfOwnTerretory() {
      return this.territoriesData.filter(data => data.familyName === this.familyName).length;
    },
    sortedBusines() {
      const businesClone = [...this.businessData];
      return businesClone.sort((a, b) => b.coords[1] - a.coords[1]);
    },
    countOfOwnBusines() {
      return this.businessData.filter(busines => busines.holdersName === this.ownerName).length;
    }
  },
  watch: {
    isShowModal() {
      this.selectedCard = this.$t("Выберите карту");
    },
    businessData() {
      setTimeout(() => {
        this.setPositionToBusiness();
      });
    },
    attackId() {
      this.attackData = this.businessData.find(busines => busines.id === this.attackId);
    }
  },
  methods: {
    showModal(id) {
      this.attackId = id;
      this.isShowModal = true;
    },
    setPositionToBusiness() {
      for (let i = 0; i < this.businessData.length; i++) {
        const {
          id,
          coords
        } = {
          ...this.businessData[i]
        };
        const businesItem = document.getElementById(id);
        if (!businesItem) {
          return;
        }
        businesItem.style.position = "absolute";
        businesItem.style.zIndex = -1;
        const minCoords = coords.map(coord => coord / 2);
        var pixel = this.map.getPixelFromCoordinate(minCoords);
        businesItem.style.left = pixel[0] + "px";
        businesItem.style.top = pixel[1] + "px";
        businesItem.style.transform = "scale(" + (Math.pow(1.2, this.map.getView().getZoom() - 14) - 0.8) + ")";
        this.map.on("postcompose", () => {
          var pixel = this.map.getPixelFromCoordinate(minCoords);
          businesItem.style.left = pixel[0] + "px";
          businesItem.style.top = pixel[1] + "px";
          businesItem.style.transform = "scale(" + (Math.pow(1.2, this.map.getView().getZoom() - 14) - 0.8) + ")";
        });
      }
    },
    closeApp() {
      this.$router.push("/");
      this.$callClient("Mafia:Map:CloseMap");
    },
    keydown(e) {
      if (e.keyCode === 27 && !window.isModalShow) {
        this.closeApp();
      }
    },
    bindClose() {
      window.addEventListener("keyup", this.keydown);
    },
    getPolygonCenter(coords) {
      let ySum = 0;
      const numVertices = coords.length;
      for (let i = 0; i < numVertices; i++) {
        ySum += coords[i][1];
      }
      const centerY = ySum / numVertices;
      return centerY;
    },
    setTimer() {
      this.timer = setInterval(() => {
        this.passedTime++;
      }, 1000);
    },
    convertToMoneyFormat(numb) {
      return new Intl.NumberFormat("de-DE").format(numb);
    },
    setData(data) {
      this.businessData = data.businessData;
      this.cartList = data.cartList;
      this.ownerName = data.ownerName;
    }
  },
  beforeUnmount() {
    this.$store.dispatch("setIsShowMap", false);
    window.removeEventListener("keyup", this.keydown);
    this.$offClient("W:RobberyMap:setData", this.setData);
  },
  mounted() {
    this.$store.dispatch("setIsShowMap", true);
    if (this.vectorLayer) {
      this.vectorLayer.forEach(layer => {
        layer.getSource().clear();
      });
    }
    this.setPositionToBusiness();
    this.$onClient("W:BusinessWar:setData", this.setData);
    this.$callClient("C:BusinessWar:Ready");
    this.setTimer();
    this.bindClose();
  }
};
