import Input from "../Components/Input.vue";
export default {
  components: {
    Input
  },
  props: {
    price: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
      numbers: [10, 50, 100, 500, 1000, 5000],
      keypadAmount: 250,
      inputAmount: "",
      isBlocked: false
    };
  },
  computed: {
    currentAmount() {
      return Number(this.inputAmount) ? Number(this.inputAmount) : this.keypadAmount;
    },
    computedPrice() {
      return this.currentAmount * this.price;
    }
  },
  methods: {
    onAmountInput(e) {
      e.stopPropagation();
      this.keypadAmount = 0;
      if (!/^[0-9]+$/.test(e.target.value)) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      if (Number(e.target.value) > 1e9) e.target.value = 1e9 - 1;
      this.inputAmount = e.target.value;
    },
    isNumberActive(number) {
      return this.keypadAmount == number;
    },
    setActiveNumber(number) {
      if (!this.isNumberActive(number)) {
        this.keypadAmount = number;
        this.inputAmount = "";
      }
    },
    onAmountChange(e) {
      if (!e.target.value || !Number(e.target.value)) {
        this.inputAmount = "";
        this.keypadAmount = this.numbers[0];
      }
    },
    onAction() {
      if (!this.isBlocked) {
        if (this.currentAmount) {
          this.$callClient("C:CasinoShop:Buy", this.currentAmount);
        }
        this.isBlocked = true;
        setTimeout(() => {
          this.isBlocked = false;
        }, 1000);
      }
    }
  }
};
