import Logo from "./Logo.vue";
import BackpackInfo from "./BackpackInfo.vue";
import ArrowSwitcher from "./ArrowSwitcher.vue";
import ColorPanel from "./Colors/ColorPanel.vue";
export default {
  components: {
    Logo,
    BackpackInfo,
    ArrowSwitcher,
    ColorPanel
  },
  props: {
    type: {
      type: String,
      required: true
    },
    shop: {
      type: String,
      required: true
    },
    activeCategory: {
      type: [Object, null],
      required: true
    },
    items: {
      type: Array,
      required: true
    },
    activeItem: {
      type: [Object, null],
      required: true
    },
    modifications: {
      type: [Array, Object],
      required: true
    },
    activeModification: {
      type: [Object, null],
      required: true
    },
    isSpecialWallet: {
      type: Boolean,
      required: true
    }
  },
  computed: {
    showBackpacks() {
      return this.type === "backpacks" && this.activeModification;
    },
    showArrows() {
      return this.type !== "clothes" && this.activeModification;
    }
  },
  methods: {
    getName(item) {
      console.log(this.type);
      if (this.type === "tattoo") {
        if (this.$t(item.name) == " ") {
          return this.$t("Кастом тату: ") + " " + item.name;
        }
      }
      return this.$t(item.name);
    },
    getKey(item) {
      return item.id + item.name;
    },
    getMoney(item) {
      return !this.isSpecialWallet ? `${item.price.toLocaleString("uk-UA")}$` : item.price.toLocaleString("uk-UA");
    },
    itemActive(item) {
      return item === this.activeItem;
    },
    setItem(item) {
      this.$emit("setItem", item);
    },
    setModification(modification) {
      this.$emit("setModification", modification);
    },
    buyItem(type) {
      this.$emit("buyItem", type);
    },
    hideApp() {
      this.$emit("hideApp");
    }
  }
};
