import SystemAppHeader from "../../../../Components/SystemAppHeader.vue";
import PhoneMixin from "../../../../Mixins/PhoneMixin";
export default {
  data() {
    return {
      playerRingtone: null,
      curretnRingtone: null,
      link: "",
      ringtoneList: [{
        name: "default",
        src: "default"
      }]
    };
  },
  watch: {
    link() {
      const audio = new Audio(this.link);
      audio.onloadeddata = () => {
        this.curretnRingtone = this.link;
        console.log("loaded");
      };
    }
  },
  methods: {
    playRingtone() {
      if (this.playerRingtone) {
        this.playerRingtone.pause();
      }
      this.playerRingtone = new Audio(this.link);
      this.playerRingtone.play();
    },
    onSave() {
      this.$callClient("W:Phone:Settings:Save", "ringtone", this.link);
      this.$store.commit("phone/setPhoneSetting", {
        ringtone: this.link
      });
      this.$router.go(-1);
    }
  },
  mixins: [PhoneMixin],
  components: {
    SystemAppHeader
  }
};
