import RanksItem from "./RanksItem.js";
import RanksItemFree from "./RanksItemFree.js";
export default {
  template: `
    <div class="controls-main__wrapper">
        <div class="controls-main__content">
            <transition-group name="controls-members-list" class="controls-main__content-list square" tag="div">
                <RanksItem 
                    v-for="index in maxRanks" 
                    :key="index"
                    v-if="rankExists(index)" 
                    :rank="rankExists(index)"
                    :style="getStyle(index)">
                </RanksItem>
                <RanksItemFree 
                    :id="index" 
                    :key="index"
                    :style="getStyle(index)"
                    v-else>
                </RanksItemFree>
            </transition-group>
        </div>
    </div>

    `,
  components: {
    RanksItem,
    RanksItemFree
  },
  data() {
    return {
      x: false
    };
  },
  props: {
    ranks: {
      type: Array
    },
    maxRanks: {
      type: Number
    }
  },
  methods: {
    rankExists(index) {
      return this.ranks.find(x => x.id == index) ? this.ranks.find(x => x.id == index) : null;
    },
    getStyle(index) {
      return `grid-column: ${index % 4 ? index % 4 : 4} ; grid-row: ${Math.ceil(index / 4)} `;
    }
  }
};
