import UniversalModal from "../../../../../../Components/UniversalModal/UniversalModal.vue";
import ItemCard from "./Components/ItemCard.vue";
export default {
  components: {
    ItemCard,
    UniversalModal
  },
  props: {
    item: {
      type: Object,
      required: true
    },
    category: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      currentBet: "",
      isBuying: false
    };
  },
  methods: {
    buyItem() {
      if (this.currentBet) {
        this.isBuying = true;
      }
    },
    confirmBuying(type) {
      this.$callClient("Auction:MakeBet", this.item.lotID, type, this.currentBet);
      this.currentBet = "";
      this.isBuying = false;
    },
    setBet(value) {
      this.currentBet = value;
    },
    onClick() {
      this.$emit("getBack");
    }
  }
};
