import MPInput from "../../MP-Input/MP-Input.vue";
import MPActionBtn from "../../MP-ActionBtn/MP-ActionBtn.vue";
import InfoBlock from "./InfoBlock.vue";
export default {
  props: {
    modalData: {
      type: Object,
      required: true
    },
    modalType: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      postedQuantityInput: null
    };
  },
  computed: {
    getModalData() {
      return [[{
        title: "Назва товару",
        value: this.modalData.name
      }]];
    },
    getIsCanUnload() {
      return this.postedQuantityInput;
    }
  },
  methods: {
    setPostedQuantity(value) {
      this.postedQuantityInput = value;
    },
    onClose() {
      this.$store.commit("marketPlace/closeModal");
    },
    onUnload() {
      this.$callClient("C:MarketPlace:Stock:Unload", this.modalData.id, this.postedQuantityInput);
    }
  },
  components: {
    MPActionBtn,
    MPInput,
    InfoBlock
  }
};
