import Input from "../Components/Input.vue";
export default {
  components: {
    Input
  },
  props: {
    price: {
      type: Number,
      required: true
    }
  },
  data() {
    return {
      inputAmount: "",
      isBlocked: false
    };
  },
  computed: {
    currentAmount() {
      return Number(this.inputAmount) ? Number(this.inputAmount) : 0;
    },
    computedPrice() {
      return this.currentAmount * this.price;
    }
  },
  methods: {
    onAmountInput(e) {
      e.stopPropagation();
      if (!/^[0-9]+$/.test(e.target.value)) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
      if (Number(e.target.value) > 1e9) e.target.value = 1e9 - 1;
      this.inputAmount = e.target.value;
    },
    onAmountChange(e) {
      if (!e.target.value || !Number(e.target.value)) {
        this.inputAmount = "";
      }
    },
    onAction() {
      if (!this.isBlocked) {
        if (this.currentAmount) {
          this.$callClient("C:CasinoShop:Sell", this.currentAmount);
        }
        this.isBlocked = true;
        setTimeout(() => {
          this.isBlocked = false;
        }, 1000);
      }
    }
  }
};
