export default {
  name: "DanceMiniGame",
  methods: {
    handleKeyDown(event) {
      if (event.key === "Escape") {
        this.$callClient("W:DanceMiniGame:Close");
      }
    }
  },
  mounted() {
    window.addEventListener("keyup", this.handleKeyDown);
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.handleKeyDown);
  }
};
