const valToDollar = value => value !== undefined && typeof value === "number" ? `${value.toLocaleString("uk-UA")} $` : ``;
const valToTime = value => {
  if (value !== undefined && typeof value === "number") {
    let hours = Math.floor(value / 3600);
    value = value - 3600 * hours;
    let minutes = Math.floor(value / 60);
    value = value - 60 * minutes;
    return `${(hours < 10 ? "0" : "") + hours.toString()}:${(minutes < 10 ? "0" : "") + minutes.toString()}:${(value < 10 ? "0" : "") + value.toString()}`;
  }
  return ``;
};
export { valToDollar, valToTime };
