      if (!this.isValid) {
        return this;
      }
      this.$callClient("C:AdvocateMenu:OfferRelease", this.AdvocateMenu.UID, this.reason, this.count);
    },
    onCancelBuy() {
      this.$callClient("C:AdvocateMenu:Leave");
    },
    setData(data) {
      this.AdvocateMenu = data;
      this.$router.push("/LawyerMenu/2");
    },
    close() {
      this.$callClient("C:AdvocateMenu:Leave");
    },
    onKeyUp(e) {
      if (e.key === "Escape") {
        this.close();
      }
    }
  },
  components: {
    Tilt
  },
  created() {
    this.$onClient("W:AdvocateMenu:setData", this.setData);
    this.$callClient("C:AdvocateMenu:Ready");
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    this.$offClient("W:AdvocateMenu:setData", this.setData);
    this.$callClient("C:AdvocateMenu:closePage");
    window.removeEventListener("keyup", this.onKeyUp);
  }
