import MemberItem from "./Components/MemberItem.vue";
import ListNavigation from "../../Components/ListNavigation.vue";
export default {
  data() {
    return {
      members: [],
      page: 1,
      pages: 10,
      membersCount: 10
    };
  },
  methods: {
    movePage(type) {
      this.$callClient("Controls:SetMembersPage", type, this.page);
    },
    setData(members) {
      this.members = members;
    },
    setRecursiveData(data) {
      this.$recursiveAssign(data, this.$data);
    },
    updateMember(newMember) {
      let index = newMember && newMember.id !== undefined && this.members && this.members.find(x => x.id === newMember.id) && this.members.indexOf(this.members.find(x => x.id === newMember.id));
      if (index !== undefined) this.members.splice(index, 1, newMember);
    }
  },
  components: {
    MemberItem,
    ListNavigation
  },
  mounted() {
    this.$onClient("Controls:UpdateMembers", this.setData);
    this.$onClient("Controls:updateMember", this.updateMember);
    this.$onClient("Controls:Members:SetData", this.setRecursiveData);
    this.$callClient("Controls:Members:Ready");
  },
  beforeUnmount() {
    this.$offClient("Controls:UpdateMembers", this.setData);
    this.$offClient("Controls:Members:SetData", this.setRecursiveData);
    this.$offClient("Controls:updateMember", this.updateMember);
  }
};
