import Sidebar from "./Sidebar.vue";
import ATMInput from "./Input.vue";
import Announcement from "./Announcement.vue";
export default {
  components: {
    Sidebar,
    ATMInput,
    Announcement
  },
  data() {
    return {
      amount: 0,
      isButtonBlocked: false
    };
  },
  methods: {
    onInput(e) {
      e.stopPropagation();
      if (!/^[0-9]+$/.test(e.target.value)) e.target.value = e.target.value.substring(0, e.target.value.length - 1);
    },
    onAmountInput(e) {
      this.onInput(e);
      if (Number(e.target.value) > 1e9) e.target.value = 1e9 - 1;
      this.amount = e.target.value;
    },
    onAmountFocus(e) {
      e.stopPropagation();
      if (this.amount == 0) this.amount = "";
    },
    onAmountBlur(e) {
      e.stopPropagation();
      if (this.amount == "") this.amount = 0;
    }
  }
};
