export default {
  data() {
    return {};
  },
  props: {
    houseData: {
      type: Object,
      required: true
    }
  },
  methods: {
    onDelete(member) {
      const UniversalModalData = {
        title: this.$t("Исключить учасника"),
        content: [{
          type: "description",
          value: this.$t("Вы действительно хотите исключить учасника <span>{0}</span>", [member.name])
        }],
        buttons: {
          submit: {
            name: this.$t("Да"),
            handler: () => {
              this.$callClient("C:HouseManager:DeleteMember", member.uuid);
            }
          },
          cansel: {
            name: this.$t("Отмена")
          }
        }
      };
      this.$UniversalModal.create(UniversalModalData);
    },
    onDeleteAll() {
      const UniversalModalData = {
        title: this.$t("Исключить всех учасников"),
        content: [{
          type: "description",
          value: this.$t("Вы действительно хотите исключить всех учасников")
        }],
        buttons: {
          submit: {
            name: this.$t("Да"),
            handler: () => {
              this.$callClient("C:HouseManager:DeleteAllMembers");
            }
          },
          cansel: {
            name: this.$t("Отмена")
          }
        }
      };
      this.$UniversalModal.create(UniversalModalData);
    }
  }
};
