import './style/reset.scss';
import { mapGetters } from "vuex";
import Navigation from "./Navigation.vue";
// import "./style/authentication.scss";

export default {
  components: {
    Navigation
  },
  data() {
    return {
      authenticationActive: false,
      previewActive: false
    };
  },
  directives: {
    play: {
      mounted(el) {
        el.play();
      }
    }
  },
  computed: {
    ...mapGetters("global", ["getCdnResolver"]),
    ...mapGetters("authentication", ["getCountdown", "getProfile"]),
    navigationHandler() {
      return !["/authentication/countdown"].includes(this.$route.path);
    },
    getRoute() {
      let route = this.$route.path.split("/");
      return route[route.length - 1];
    },
    getPreview() {
      return `${this.getCdnResolver.cdnLink}/preview.webm`;
    }
  },
  methods: {
    closeAuthentication() {
      this.$callClient("Authentication.Close");
    },
    previewSkip() {
      if (this.previewActive) {
        this.previewActive = false;
      }
    }
  },
  created() {
    document.addEventListener("keypress", this.previewSkip);
    document.addEventListener("click", this.previewSkip);
  },
  beforeMount() {
    this.$RPC.call("Authentication.GetCountdown").then(([status, data]) => {
      if (status) {
        this.getCountdown.time = data.time;
        this.getCountdown.title = data.title;
        this.getCountdown.subtitle = data.subtitle;
        this.getCountdown.timerTitle = data.timerTitle;
      }
    });
  },
  mounted() {
    this.authenticationActive = true;
    this.previewActive = true;
  },
  beforeUnmount() {
    document.removeEventListener("keypress", this.previewSkip);
    document.removeEventListener("click", this.previewSkip);
  }
};
