export default {
  props: {
    title: {
      type: String,
      required: false
    },
    subtitle: {
      type: String,
      required: false
    }
  },
  methods: {
    onClick() {
      this.$emit("close");
    },
    onKeyUp(e) {
      if (e.keyCode === 27 && !window.isModalShow) this.onClick();
    }
  },
  created() {
    window.addEventListener("keyup", this.onKeyUp);
  },
  beforeUnmount() {
    window.removeEventListener("keyup", this.onKeyUp);
  }
};
