import { mapGetters } from "vuex";
import axios from "axios";
export default {
  computed: {
    ...mapGetters("global", ["getCdnResolver"]),
    ...mapGetters("authentication", ["getProfile"])
  },
  methods: {
    async APIgetNewsRequest(query = "") {
      let response = await axios({
        method: "GET",
        url: `${this.getCdnResolver.serverLink}/api/news/get${query}`
      });
      return response.data;
    },
    async APIuserRecovery(email) {
      let response = await axios({
        method: "POST",
        url: `https://s1.nexus-rp.net/api/user/recovery?locale=${this.$i18n.locale}`,
        data: {
          email
        }
      });
      return response.data;
    },
    async APIuserRecoveryConfirm(email, code, newPassword) {
      let response = await axios({
        method: "POST",
        url: `https://s1.nexus-rp.net/api/user/recovery/confirm?locale=${this.$i18n.locale}`,
        data: {
          email,
          code,
          newPassword
        }
      });
      return response.data;
    },
    async APIgetPayWayTypes() {
      let response = await axios({
        method: "GET",
        url: `${this.getCdnResolver.serverLink}/api/payment/getPayWayTypes`
      });
      return response.data;
    },
    async APIpayment(merchant, amount) {
      let response = await axios({
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        url: `https://s1.nexus-rp.net/api/payment?locale=${this.$i18n.locale}`,
        data: {
          id: 1,
          login: this.getProfile.login,
          count: amount
        }
      });
      return response.data;
    }
  }
};
