import MPNavigation from "./Components/MP-Navigation/MP-Navigation.vue";
import MPHeader from "./Components/MP-Header/MP-Header.vue";
import MPContent from "./Components/MP-Content/MP-Content.vue";
import MPModal from "./Components/MP-Modal/MP-Modal.vue";
export default {
  props: {},
  components: {
    MPNavigation,
    MPHeader,
    MPContent,
    MPModal
  },
  data() {
    return {
      isOpen: false
    };
  },
  methods: {
    setData(data) {
      this.$store.commit("marketPlace/setData", data);
    },
    setListData(data) {
      this.$store.commit("marketPlace/setListData", data);
    },
    onClose(e) {
      if (e.key === "Escape" && this.isOpen) {
        this.$callClient("C:MarketPlace:Close");
      }
    }
  },
  computed: {
    getModalData() {
      return this.$store.getters["marketPlace/getModalData"];
    }
  },
  mounted() {
    this.isOpen = true;
    this.$onClient("W:MarketPlace:SetData", this.setData);
    this.$callClient("C:MarketPlace:Ready");
    setTimeout(() => {
      window.addEventListener("keyup", this.onClose);
    }, 300);
    if (this.$store.getters["marketPlace/getCardsList"]("stock").length) {
      this.$store.commit("marketPlace/openModal", [{}, "stockNotify"]);
    }
  },
  beforeUnmount() {
    this.$offClient("W:MarketPlace:SetData", this.setData);
    window.removeEventListener("keyup", this.onClose);
  },
  beforeRouteLeave(to, from, next) {
    this.$store.commit("marketPlace/closeMarketPlace");
    this.isOpen = false;
    next();
  }
};
