import MPItemsCardList from "../../Components/MP-ItemsCardList/MP-ItemsCardList.vue";
import MPOpenedItem from "../../Components/MP-OpenedItem/MP-OpenedItem.vue";
export default {
  components: {
    MPItemsCardList,
    MPOpenedItem
  },
  data() {
    return {
      defaultServices: [{
        id: -1,
        objectType: "service",
        name: "Створити оголошення",
        status: "available",
        action: "emptyListing",
        img: "createListing",
        data: {}
      }, {
        id: -2,
        objectType: "transportRent",
        name: "Здати транспорт в оренду",
        status: "available",
        action: "transportRent",
        img: "transportRent",
        data: {}
      }]
    };
  },
  watch: {
    getTransportCardsLength: {
      handler(newValue) {
        this.defaultServices[1].status = newValue ? "available" : "unavailable";
      },
      immediate: true
    }
  },
  computed: {
    getTransportCardsLength() {
      return this.getCardsList.filter(itemCard => itemCard.objectType === "car").length;
    },
    getSelectedItem() {
      return this.$store.getters["marketPlace/getSelectedItem"];
    },
    getCardsList() {
      const playerItemsCard = this.$store.getters["marketPlace/getCardsList"]("createListing");
      const stockItemsCard = this.$store.getters["marketPlace/getCardsList"]("stock");
      if (this.$route.params.type === "all") {
        return [...this.defaultServices, ...playerItemsCard, ...stockItemsCard];
      } else if (this.$route.params.type === "transportRent") {
        return playerItemsCard.filter(itemCard => itemCard.objectType === "car");
      }
      return [];
    }
  }
};
