export default {
  data() {
    return {
      isShowOptions: false
    };
  },
  props: {
    selectedValue: String,
    options: Object,
    disabled: {
      type: Boolean,
      default: false
    }
  },
  emits: ["update:selectedValue"],
  watch: {
    options(value) {
      if (!value.length) {
        this.isShowOptions = false;
      }
    }
  },
  methods: {
    setValue(option) {
      this.$emit("update:selectedValue", option);
    },
    showOptions() {
      if (this.disabled) {
        return;
      }
      this.isShowOptions = !this.isShowOptions;
    }
  }
};
